var mongoose = require("mongoose");
var schemaObj = new mongoose.Schema({
    uid: { type: Number, index: true, unique: true },
    name: { type: String },
    avatar: { type: String },
    level: { type: Number },
    serverId: { type: Number, index: true },
    serverName: { type: String },
    exp: { type: Number },
    sdkInfo: {},
    gold: { type: Number },
    gem: { type: Number },
    createAt: { type: Date },
    updateAt: { type: Date },
    totalRecharge: { type: Number },
    lastRechargeTime: { type: Date },
    lastLoginTime: { type: Date },
    lastLogoutTime: { type: Date },
    activetyList: {},
    item: [{ type: String, ref: 'item' }],
    hero: [{ type: String, ref: 'hero' }] // 英雄关联表
});
schemaObj.post("save", function (doc, next) {
    doc.createAt = new Date();
    doc.updateAt = new Date();
    doc.lastLoginTime = new Date();
    doc.activetyList = {};
    doc.save();
    next();
});
module.exports = schemaObj;
